import { Formik, Form, Field } from "formik";
import * as yup from "yup";
import { gql } from "apollo-boost";
import { useQuery } from "@apollo/react-hooks";

const COUNTRY_INFO = gql`
  {
    country(code: "CZ") {
      name
      native
      emoji
      currency
      languages {
        code
        name
      }
    }
  }
`;

// const MyRadio ({label, ...props}) => {
//     const [field, meta] = useField(props)
//     return (
//         <FormControlLabel control={<Radio />} label={label} />
//     )
// }

// string.min(limit: number | Ref, message?: string | function): Schema

yup.setLocale({
  string: {
    max: "Je to dlouhý"
  }
});

let schema = yup.object().shape({
  firstName: yup
    .string()
    .required()
    .max(10)
});

const country = () => {
  const { loading, error, data } = useQuery(COUNTRY_INFO);

  if (loading) return <p>Loading...</p>;
  if (error) return console.log(error);

  return <p>{data.country.name}</p>;
};

const Index: React.FC = () => {
  return (
    <>
      {country()}
      <Formik
        initialValues={{
          firstName: "",
          lastName: "",
          isTall: false,
          cookies: [],
          yogurt: ""
        }}
        onSubmit={(data, { setSubmitting, resetForm }) => {
          setSubmitting(true);
          console.log("submit: ", data);
          setSubmitting(false);
          resetForm();
        }}
        validationSchema={schema}
      >
        {({ errors, touched, values, setSubmitting }) => (
          <Form>
            <Field type="text" name="firstName" />
            {errors.firstName && touched.firstName ? (
              <div>{errors.firstName}</div>
            ) : null}
            <Field type="text" name="lastName" />
            <Field name="isTall" type="checkbox" />
            <div className="">Cookies:</div>
            <Field name="cookies" type="checkbox" value="chocolate chip" />
            <Field name="cookies" type="checkbox" value="snickerdoodle" />
            <Field name="cookies" type="checkbox" value="sugar" />
            <div className="">Yogurt:</div>
            <Field name="yogurt" type="radio" value="peach" />
            <Field name="yogurt" type="radio" value="apple" />
            <Field name="yogurt" type="radio" value="strawberry" />
            <button type="submit" disabled={!!!setSubmitting}>
              Submit
            </button>
            <pre>{JSON.stringify(values, null, 2)}</pre>
          </Form>
        )}
      </Formik>
    </>
  );
};

export default Index;
